/* GOOGLE FONTS */

@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700;800&display=swap');

/* VARIABLES */


    $header-height: 3rem;
    $hue-color: 190;

    $first-color: hsl(($hue-color), 64%, 22%);
    $first-color-second: hsl(($hue-color), 64%, 22%);
    $first-color-alt: hsl(($hue-color), 64%, 15%);
    $title-color: hsl(($hue-color), 64%, 18%);
    $text-color: hsl(($hue-color), 24%, 35%);
    $text-color-light: hsl(($hue-color), 8%, 60%);
    $input-color: hsl(($hue-color), 24%, 97%);
    $body-color: hsl(($hue-color), 100%, 99%);
    $white-color: #FFF;

    $scroll-bar-color: hsl(($hue-color), 12%, 90%);
    $scroll-thumb-color: hsl(($hue-color), 12%, 75%);

    $body-font: 'Poppins', sans-serif;
    $title-font: 'Poppins', sans-serif;

    $biggest-font-size: 2.5rem;
    $h1-font-size: 1.5rem;
    $h2-font-size: 1.25rem;
    $h3-font-size: 1rem;
    $normal-font-size: .938rem;
    $small-font-size: .813rem;
    $smaller-font-size: .75rem;

    @media screen and (min-width: 968px){
        $biggest-font-size: 4rem;
        $h1-font-size: 2.25rem;
        $h2-font-size: 1.75rem;
        $h3-font-size: 1.25rem;
        $normal-font-size: 1rem;
        $small-font-size: .875rem;
        $smaller-font-size: .813rem;
    }

    $font-medium: 500;
    $font-semi-bold: 600;

    $mb-0-25: .25rem;
    $mb-0-5: .5rem;
    $mb-0-75: .75rem;
    $mb-1: 1rem;
    $mb-1-25: 1.25rem;
    $mb-1-5: 1.5rem;
    $mb-2: 2rem;
    $mb-2-5: 2.5rem;

    $z-tooltip: 10;
    $z-fixed: 100;

    $img-transition: .3s;
    $img-hidden: hidden;
    $img-scale: scale(1.1);


/*========== Variables Dark theme ==========*/
body.dark-theme{
    $first-color-second: hsl(($hue-color), 54%, 12%);
    $title-color: hsl(($hue-color), 24%, 95%);
    $text-color: hsl(($hue-color), 8%, 75%);
    $input-color: hsl(($hue-color), 29%, 16%);
    $body-color: hsl(($hue-color), 29%, 12%);

    $scroll-bar-color: hsl(($hue-color), 12%, 48%);
    $scroll-thumb-color: hsl(($hue-color), 12%, 36%);
}

/*========== Button Dark/Light ==========*/
.nav__dark{
    display: flex;
    align-items: center;
    column-gap: 2rem;
    position: absolute;
    left: 3rem;
    bottom: 4rem;
}

.change{
    &-theme,
    &-theme-name{
        color: $text-color;
    }
    &-theme{
        cursor: pointer;
        font-size: 1rem;
    }
    &-theme-name{
        font-size: $small-font-size;
    }
}


/* BASE */

* {
    box-sizing: border-box;
    padding: 0;
    margin: 0;
}

html {
    scroll-behavior: smooth;
}

body {
    margin: $header-height 0 0 0;
    font-family: $body-font;
    font-size: $normal-font-size;
    background-color: $body-color;
    color: $text-color;
}

h1,h2,h3 {
    color: $title-color;
    font-weight: $font-semi-bold;  
    font-family: $title-font;
}

ul {
    list-style: none;
}

a {
    text-decoration: none;
}

img,
video {
    max-width: 100%;
    height: auto;
}

button,
input {
    border: none;
    font-family: $body-font;
    font-size: $normal-font-size;
}

button{
    cursor: pointer;
}

input{
    outline: none;
}

.main{
    overflow-x: hidden;
}

/* REUSABLE CLASSES */

.section{
    padding: 4.5rem 0 2.5rem;

    .section__title{
        font-size: $h2-font-size;
        color: $title-color;
        text-align: center;
        text-transform: capitalize;
        margin-bottom: $mb-2;
    }
}

.container {
    max-width: 968px;
    margin-left: $mb-1;
    margin-right: $mb-1;
}

.grid {
    display: grid;
    gap: 1.5rem;
}

/* HEADER SECTION */

.header{
    width: 100%;
    position: fixed;
    top: 0;
    left: 0;
    z-index: $z-fixed;
    background-color: transparent;
}

.nav{
    height: 3rem;
    display: flex;
    justify-content: space-between;
    align-items: center;

    &__logo,
    &__toggle{
        color: $white-color;
    }
    &__logo{
        font-weight: $font-semi-bold;
    }
    &__toggle{
        font-size: 1.2rem;
        cursor: pointer;
    }

    &__menu{
        position: relative;

        @media screen and (max-width: 767px) {
            position: fixed;
            background-color: $body-color;
            top: 0;
            right: -100%;
            width: 70%;
            height: 100%;
            box-shadow: -1px 0 4px hsla(190, 64%, 15%, .15);
            padding: 3rem;
            transition: .4s;
        }
    }
    &__list{
        display: flex;
        flex-direction: column;
        row-gap: 2.5rem;
    }
    &__link{
        color: $text-color-light;
        font-weight: $font-semi-bold;
        text-transform: uppercase;

        &:hover{
            color: $text-color;
        }
    }
    &__close{
        position: absolute;
        top: .75rem;
        right: 1rem;
        font-size: 1.5rem;
        color: $title-color;
        cursor: pointer;
    }
}

.show-menu{
    right: 0;
}

.scroll-header{
    background-color: $body-color;
    box-shadow: 0 0 4px hsla(190, 64%, 15%, .15);

    & .nav__logo,
    & .nav__toggle{
        color: $title-color;
    }
}

.active-link{
    position: relative;
    color: $title-color;

    &::before{
        content: '';
        position: absolute;
        background-color: $title-color;
        width: 100%;
        height: 2px;
        bottom: -.75rem;
        left: 0;
    }
}

/* HOME SECTION */

.home{
    &__img{
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100vh;
        object-fit: cover;
        object-position: 83%;
    }
    &__container{
        position: relative;
        height: calc(100vh - 3rem);
        align-content: center;
        row-gap: 5rem;
    }
    &__data-subtitle,
    &__data-title,
    &__social-link,
    &__info{
        color: $white-color;
    }
    &__data{
        &-subtitle{
            display: block;
            font-weight: $font-semi-bold;
            margin-bottom: $mb-0-75;
        }
        &-title{
            font-size: $biggest-font-size;
            font-weight: $font-medium;
            margin-bottom: $mb-2-5;
        }
    }
    &__social{
        display: flex;
        flex-direction: column;
        row-gap: 1.5rem;

        &-link{
            font-size: 1.2rem;
            width: max-content;
        }
    }
    &__info{
        background-color: $first-color;
        display: flex;
        padding: 1.5rem 1rem;
        align-items: center;
        column-gap: .5rem;
        position: absolute;
        right: 0;
        bottom: 1rem;
        width: 228px;

        &-title{
            display: block;
            font-size: $small-font-size;
            font-weight: $font-semi-bold;
            margin-bottom: $mb-0-75;
        }
        &-button{
            font-size: $smaller-font-size;
        }
        &-overlay{
            overflow: $img-hidden;
        }
        &-img{
            width: 145px;
            transition: $img-transition;

            &:hover{
                transform: $img-scale;
            }
        }
    }
}

/* BUTTONS */

.button{
    display: inline-block;
    background-color: $first-color;
    color: $white-color;
    padding: 1rem 2rem;
    font-weight: $font-semi-bold;
    transition: .3s;
    
    &:hover{
        background-color: $first-color-alt;
    }
    
    &--flex{
        display: flex;
        align-items: center;
        column-gap: .25rem;
    }
    &--link{
        background: none;
        padding: 0;
        
        &:hover{
            background: none;
        }
    }
}

/* ABOUT SECTION */

.about{
    &__data{
        text-align: center;
    }
    &__container{
        row-gap: 2.5rem;
    }
    &__description{
        margin-bottom: $mb-2;
    }
    &__img{
        display: flex;
        column-gap: 1rem;
        align-items: center;
        justify-content: center;

        &-overlay{
            overflow: $img-hidden;
        }

        &-one{
            width: 130px;
        }
        &-two{
            width: 180px;
        }
        &-one,
        &-two{
            transition: $img-transition;
        }
        &-one:hover,
        &-two:hover{
            transform: $img-scale;
        }
    }
}

/* DISCOVER SECTION */

.discover{
    &__card{
        position: relative;
        width: 200px;
        overflow: $img-hidden;
    }
    &__data{
        position: absolute;
        bottom: 1.5rem;
        left: 1rem;
    }
    &__title,
    &__description{
        color: $white-color;
    }
    &__title{
        font-size: $h3-font-size;
        margin-bottom: $mb-0-25;
    }
    &__description{
        display: block;
        font-size: $smaller-font-size;
    }
    &__img{
        transition: $img-transition;

        &:hover{
            transform: $img-scale;
        }
    }
}

.swiper-container-3d .swiper-slide-shadow-left,
.swiper-container-3d .swiper-slide-shadow-right{
    background-image: none;
}


