.scrollup {
    position: fixed;
    right: 1rem;
    bottom: -20%;
    background-color: $first-color;
    padding: .5rem;
    display: flex;
    opacity: .9;
    z-index: $z-tooltip;
    transition: .4s;

    &:hover{
        background-color: $first-color-alt;
        opacity: 1;
    }

    &__icon{
        color: $white-color;
        font-size: 1.2rem;
    }
}

.show-scroll {
    bottom: 5rem;
}

/* SCROLL BAR */
::-webkit-scrollbar {
    width: .60rem;
    background-color: $scroll-bar-color;
}
  
::-webkit-scrollbar-thumb {
    background-color: $scroll-thumb-color;
}
  
::-webkit-scrollbar-thumb:hover {
    background-color: $text-color-light;
}

/* MEDIA QUERIES */

@media screen and (max-width: 340px){
    .home{
        &__info{
            width: 190px;
            padding: 1rem;
        }
    }
}


@media screen and (min-width: 768px){
    body{
        margin: 0;
    }

    .nav{
        height: calc(3rem + 1.5rem);

        &__link{
            color: $white-color;
            text-transform: initial;

            &:hover{
                color: $white-color;
            }
        }
        &__dark{
            position: initial;
        }
        &__menu{
            display: flex;
            column-gap: 1rem;
        }
        &__list{
            flex-direction: row;
            column-gap: 4rem;
        }
        &__toggle,
        &__close{
            display: none;
        }
    }

    .change{
        &-theme-name{
            display: none;
        }
        &-theme{
            color: $white-color;
        }
    }

    .active-link::before{
        background-color: $white-color;
    }

    .scroll-header .nav__link{
        color: $text-color;
    }
    .scroll-header .active-link{
        color: $title-color;
    }
    .scroll-header .active-link::before{
        background-color: $title-color;
    }

    .scroll-header .change-theme{
        color: $text-color;
    }

    .section{
        padding: 14rem 0 5rem;
    }

    .home{
        &__container{
            height: 100vh;
            grid-template-rows: 1.8fr .5fr;
        }
        &__data{
            align-self: flex-end;
        }
        &__social{
            flex-direction: row;
            align-self: flex-end;
            margin-bottom: 3rem;
            column-gap: 2.5rem;
        }
        &__info{
            bottom: 3rem;
        }
    }

    .about{
        &__container{
            grid-template-columns: repeat(2, 1fr);
            align-items: center;
        }
        &__data,
        &__title {
            text-align: initial;
        }
        &__title{
            margin-bottom: $mb-1-5;
        }
        &__description{
            margin-bottom: $mb-2;
        }
    }

    .discover{
        &__container{
            width: 610px;
            margin-left: auto;
            margin-right: auto;
        }
    }

    .discover__container,
    .place__container{
        padding-top: 2rem;
    }
}

@media screen and (min-width: 1024px){
    .container{
        margin-left: auto;
        margin-right: auto;
    }

    .home{
        &__container{
            grid-template-rows: 2fr .5fr;
        }
        &__info{
            width: 328px;
            grid-template-columns: 1fr 2fr;
            column-gap: 2rem;

            &-title{
                font-size: $normal-font-size;
            }
            &-img{
                width: 240px;
            }
        }
    }

    .about{
        &__img{
            &-one{
                width: 230px;
            }
            &-two{
                width: 290px;
            }
        }
    }

    .discover{
        &__card{
            width: 237px;
        }
        &__container{
            width: 700px;
        }
        &__data{
            left: 1.5rem;
            bottom: 2rem;
        }
        &__title{
            font-size: $h2-font-size;
        }
    }
}

@media screen and (min-width: 1200px){
    .container{
        max-width: 1024px;
    }
}
